import UserData from './components/UserData/UserData';

import React, { useEffect, useState } from 'react';

function App() {


  let URL = 'https://geektrust.s3-ap-southeast-1.amazonaws.com/adminui-problem/members.json';


  const [fetchedData, setUserDetails] = useState([]);
  const [mainApiData, setApiData] = useState([]);



  useEffect(() => {
    fetchData()
  }, [])

  const fetchData = () => {
    fetch(URL)
      .then((res) => {
        if (res.ok) return res.json();
        throw new Error('something went wrong while requesting posts');
      })
      .then((response) => {
        // console.log(response);
        setUserDetails(response);
        setApiData(response);
      })

  }

  const deleteHandler = (id) => {
    const val = fetchedData.filter((item) => item.id !== id);
    setUserDetails(val);
    setApiData(val);
  }
  const filterUser = (event) => {

    if (event.target.value.length === 0) {
      // fetchData();
    } else {

      const filter = mainApiData.filter(item => (
        item.name.includes(event.target.value) ||
        item.email.includes(event.target.value) ||
        item.role.includes(event.target.value)
      ));
      setUserDetails(filter);
    }
  }


  return (
    <div>
      <input type='text' className='searchBox' placeholder="Search by name, email or role" onChange={filterUser} />
      <UserData items={fetchedData} ondeleteHandler={deleteHandler}></UserData>
      {fetchedData.length === 0 && <h2 >No Record Found</h2>}
    </div>
  );
}

export default App;
