import SpecificUserData from '../SpecificUserData/SpecificUserData';
import React, { useEffect, useState } from 'react';
import style from './UserData.module.css';


function UserData(props) {

    const [pages] = useState(Math.round(props.items.length / props.dataLimit));
    const [currentPage, setCurrentPage] = useState(1);

    function goToNextPage() {
        setCurrentPage((page) => page + 1);
    }

    function goToPreviousPage() {
        setCurrentPage((page) => page - 1);
    }

    function changePage(event) {
        const pageNumber = Number(event.target.textContent);
        setCurrentPage(pageNumber);
    }

    const getPaginatedData = () => {
        const startIndex = currentPage * props.dataLimit - props.dataLimit;
        const endIndex = startIndex + props.dataLimit;
        return props.items.slice(startIndex, endIndex);
    };

    const getPaginationGroup = () => {
        let start = Math.floor((currentPage - 1) / props.pageLimit) * props.pageLimit;
        return new Array(props.pageLimit).fill().map((_, idx) => start + idx + 1);
    };

    return (
        <div className={style['table-container']}>
            <table>
                <thead>
                    <tr>
                        <th><input type='checkbox' /></th>
                        <th>Name</th>
                        <th>Email</th>
                        <th>Role</th>
                        <th></th>
                        <th></th>

                    </tr>
                </thead>
                {getPaginatedData().map((user) => (
                    <SpecificUserData
                        key={user.id}
                        id={user.id}
                        name={user.name}
                        email={user.email}
                        role={user.role}
                        deleteSpecificUser={props.ondeleteHandler}
                    />

                ))}

                {/* {props.items.slice(0, 10).map((user) => (
                    <SpecificUserData
                        key={user.id}
                        id={user.id}
                        name={user.name}
                        email={user.email}
                        role={user.role}
                        deleteSpecificUser={props.ondeleteHandler}
                    />

                ))} */}
            </table>

            <div className={style.pagination}>
                {/* previous button */}
                <button
                    onClick={goToPreviousPage}
                    className={`{{style.prev}} ${currentPage === 1 ? 'disabled' : ''}`}
                >
                    prev
                </button>

                {/* show page numbers */}
                {getPaginationGroup().map((item, index) => (
                    <button
                        key={index}
                        onClick={changePage}
                        className={`{{style.paginationItem}} ${currentPage === item ? 'active' : null}`}
                    >
                        <span>{item}</span>
                    </button>
                ))}

                {/* next button */}
                <button
                    onClick={goToNextPage}
                    className={`{{style.next}} ${currentPage === pages ? 'disabled' : ''}`}
                >
                    next
                </button>
            </div>

        </div>
    );
}

export default UserData;
