import UserData from './components/UserData/UserData';

import React, { useEffect, useState } from 'react';
// import Pagination from './components/Pagination/Pagination';
// import SpecificUSerData from './components/SpecificUserData/SpecificUserData';

function App() {


  let URL = 'https://geektrust.s3-ap-southeast-1.amazonaws.com/adminui-problem/members.json';


  const [fetchedData, setUserDetails] = useState([]);
  const [mainApiData, setApiData] = useState([]);
  const [error, setError] = useState('');


  useEffect(() => {
    fetchData()
  }, [])

  const fetchData = () => {
    fetch(URL)
      .then((res) => {
        if (res.ok) return res.json();
        throw new Error('something went wrong while requesting posts');
      })
      .then((response) => {
        // console.log(response);
        setUserDetails(response);
        setApiData(response);
      })
      .catch((error) => setError(error.message));

  }

  const deleteHandler = (id) => {
    const val = fetchedData.filter((item) => item.id !== id);

    setApiData(val);
    setUserDetails(val);
  }

  const onSelectedDeleteHandler = (idList) => {

    for (let id of idList) {

      console.log(mainApiData);
      deleteHandler(id);
      // const val = fetchedData.filter((item) =>
      //   item.id !== id

      // );
      // setUserDetails(val);

      // setApiData(val);
    }
  }

  const filterUser = (event) => {

    if (event.target.value.length === 0) {
      // fetchData();
      // setUserDetails(fetchData);
      // setApiData(mainApiData);
    } else {

      const filter = mainApiData.filter(item => (
        item.name.includes(event.target.value) ||
        item.email.includes(event.target.value) ||
        item.role.includes(event.target.value)
      ));
      setUserDetails(filter);
    }
  }

  if (error) return <h1>{error}</h1>;

  return (
    <div>
      <input
        type='text'
        className='searchBox'
        placeholder="Search by name, email or role"
        onChange={filterUser}
      />

      <UserData
        items={fetchedData}
        ondeleteHandler={deleteHandler}
        pageLimit={5}
        dataLimit={10}
        selectedDeleteHandler={onSelectedDeleteHandler}>
      </UserData>

      {fetchedData.length === 0 && <h2 >No Record Found</h2>}

      {/* <Pagination
        data={fetchedData}
        pageLimit={5}
        dataLimit={10}
        RenderComponent={SpecificUSerData}
        ondeleteHandler={deleteHandler} /> */}
    </div>
  );
}

export default App;
