// import './SpecificUSerData.css';
import { AiOutlineDelete } from "react-icons/ai";
import { BiEdit } from "react-icons/bi";
// import style from './SpecificUserData.module.css';
import './SpecificUserData.css';
import React, { useState } from 'react';

function SpecificUSerData(props) {
    // console.log(props);
    const [isEdit, setIsEdit] = useState(false);
    // const [isChecked, setIdTrack] = useState('');
    // const [inputStyle, setInputStyle] = useState('');
    const [isChecked, setIsChecked] = useState([]);
    const deleteUser = () => {
        props.deleteSpecificUser(props.id);
    }
    const checkBox = () => {
        if (props.id in isChecked) {
            isChecked.re
        }
        props.insertCheckedId(props.id);
        setIsChecked();
        // console.log(props.id);
    }

    const onEdit = () => {
        setIsEdit(true);
    }
    // console.log(isEdit);

    return (
        <tbody className='tableBody'>
            <tr>
                <td><input
                    name={props.id}
                    type='checkbox'
                    onClick={checkBox} />
                </td>
                <td><input
                    type='text'
                    defaultValue={props.name}
                    className={`input ${isEdit ? 'postInputStyle' : 'preInputStyle'}`}
                    disabled={`${isEdit ? '' : 'disabled'}`} />
                </td>
                <td><input
                    type='text'
                    defaultValue={props.email}
                    className={`input ${isEdit ? 'postInputStyle' : 'preInputStyle'}`}
                    disabled={`${isEdit ? '' : 'disabled'}`} />
                </td>
                <td><input
                    type='text'
                    defaultValue={props.role}
                    className={`input ${isEdit ? 'postInputStyle' : 'preInputStyle'}`}
                    disabled={`${isEdit ? '' : 'disabled'}`} />
                </td>
                <td><AiOutlineDelete
                    className="deleteBtn"
                    onClick={deleteUser} />
                    <BiEdit
                        className="editBtn"
                        onClick={onEdit} />
                </td>
                {/* <td><BiEdit
                    onClick={onEdit} />
                </td> */}
            </tr>
        </tbody>
    );
}

export default SpecificUSerData;
