// import './SpecificUSerData.css';
import { AiOutlineDelete } from "react-icons/ai";
import { BiEdit } from "react-icons/bi";
import style from './SpecificUserData.module.css';
import React, { useEffect, useState } from 'react';

function SpecificUSerData(props) {
    // console.log(props);
    const [isEdit, setIsEdit] = useState(false);
    const deleteUser = () => {
        props.deleteSpecificUser(props.id);

    }
    const checkBox = () => {
        props.insertCheckedId(props.id);
    }

    const onEdit = () => {
        setIsEdit(true);
    }

    return (
        <tbody className={style.tableBody}>
            <tr>
                <td><input type='checkbox' onClick={checkBox} /></td>
                <td><input type='text' value={props.name} className={`${isEdit ? '' : 'disabled'}`} /></td>
                <td><input type='text' value={props.email} disabled /></td>
                <td><input type='text' value={props.role} disabled /></td>
                <td><AiOutlineDelete onClick={deleteUser} /></td>
                <td><BiEdit onClick={onEdit} /></td>
            </tr>
        </tbody>
    );
}

export default SpecificUSerData;
